		hpFrame->setSize(SDL_Rect{ portrait->getSize().x + portrait->getSize().w - 4, 16, entry->getSize().w - (portrait->getSize().x + portrait->getSize().w - 4), hpHeight });
		SDL_Rect hpFramePos = hpFrame->getSize();
		hpFrame->setHollow(true);

