					//		}
					//	}
					//	SDL_Rect posTitle = effectTxt->getSize();
					//	int scrollTitleLength = titleWidth - effectTxtFrame->getSize().w;
					//	if ( titleWidth <= effectTxtFrame->getSize().w )
					//	{
